"use strict";document.addEventListener("DOMContentLoaded",()=>{const mailButton=document.querySelector(".mail-btn");if(!mailButton){return}
mailButton.addEventListener("click",(event)=>{event.preventDefault();const button=event.currentTarget;const form=button.closest("form");if(!form)return;if(form.checkValidity()){button.classList.add("fly");const formData=new FormData(form);fetch(form.action,{method:form.method,body:formData,headers:{Accept:"application/json"},}).then((response)=>{if(response.ok){console.log("Formulário enviado com sucesso!")}else{console.error("Erro ao enviar o formulário.");alert("Houve um problema ao enviar o formulário.")}}).catch((error)=>{console.error("Erro de rede:",error);alert("Não foi possível enviar o formulário. Verifique sua conexão.")});setTimeout(()=>{button.classList.remove("fly");form.reset()},3400)}else{form.reportValidity()}})})