"use strict";document.addEventListener("DOMContentLoaded",()=>{const revealFromBottomElements=document.querySelectorAll(".js-reveal-from-bottom");const bandaSection=document.getElementById("banda");const revealObserverOptions={root:null,threshold:0.1};const revealObserver=new IntersectionObserver((entries,observer)=>{entries.forEach((entry)=>{if(entry.isIntersecting){if(entry.target.id==="banda"){entry.target.classList.add("reveal")}else{entry.target.classList.add("is-visible")}
observer.unobserve(entry.target)}})},revealObserverOptions);revealFromBottomElements.forEach((el)=>{revealObserver.observe(el)});if(bandaSection){revealObserver.observe(bandaSection)}})